'use strict';

exports.name = 'PalletKind';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    name: {
      type: String,
      trim: true,
      required: true
    },
    description: {
      type: String,
      trim: true
    },
    no: {
      type: String,
      trim: true
    },
    image: {
      type: String,
      trim: true
    },
    position: {
      type: Number,
      default: 1,
      min: 1
    }
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'palletKinds';
  schema.statics.BROWSE_LIMIT = 1000;

  return schema;
};
