'use strict';

const mongoose = require('mongoose');

Object.assign(exports, require('util/createDictionaryModule')(
  require('./models/partner'),
  'partners'
));

exports.modelPlugins = {
  User: (app, module, userSchema) =>
  {
    userSchema.path('partner', {
      type: mongoose.Schema.Types.ObjectId,
      default: null,
      ref: 'Partner'
    });

    userSchema.index({partner: 1});

    userSchema.statics.dataAnonymizers.push(user =>
    {
      user.partner = null;
    });

    userSchema.statics.EDITABLE_PROPERTIES.partner = true;
  }
};
