'use strict';

exports.name = 'Partner';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    name: {
      type: String,
      trim: true,
      required: true
    },
    address: {
      type: String,
      trim: true
    },
    clientNo: {
      type: String,
      trim: true
    },
    supplierColor: {
      type: String,
      trim: true
    },
    receiverColor: {
      type: String,
      trim: true
    },
    autoGdn: {
      type: Boolean,
      default: false
    },
    autoGdnPartners: [mongoose.Schema.Types.ObjectId],
    autoGrn1: {
      type: Boolean,
      default: false
    },
    autoGrn1Partners: [mongoose.Schema.Types.ObjectId],
    autoGrn2: {
      type: Boolean,
      default: false
    },
    autoGrn2Partner: {
      type: mongoose.Schema.Types.ObjectId,
      default: null
    },
    autoGrn2Partners: [mongoose.Schema.Types.ObjectId]
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'partners';
  schema.statics.BROWSE_LIMIT = 1000;
  schema.statics.PRIVILEGES_MANAGE = ['DICTIONARIES:MANAGE', 'REGISTRY:MANAGE'];

  return schema;
};
