'use strict';

const validateGoods = require('./util/validateGoods');
const createChangeSchema = require('./util/createChangeSchema');

exports.name = 'Ob';

exports.setUp = (app, mongoose) =>
{
  const obChangeSchema = createChangeSchema(mongoose);

  const obSchema = new mongoose.Schema({
    partner: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'Partner',
      required: true
    },
    date: {
      type: Date,
      required: true
    },
    goods: {},
    createdAt: Date,
    creator: {},
    updatedAt: Date,
    updater: {},
    changes: [obChangeSchema]
  }, {
    id: false,
    minimize: false
  });

  obSchema.statics.TOPIC_PREFIX = 'registry.ob';

  obSchema.index({date: -1});
  obSchema.index({partner: 1, date: -1});

  obSchema.path('goods').validate(validateGoods, 'goods:required');

  mongoose.model(exports.name, obSchema);
};
